def validate_cart_items(items: list[str]) -> bool:
    '''Validate a list of shopping cart item identifiers.

    The cart is considered valid when:
    - items is a list instance
    - the number of items is between 1 and 10 inclusive
    - every item is a string whose stripped value is not empty

    Any violation returns False. The function is designed not to raise
    just because of invalid input types or values.
    '''
    if not isinstance(items, list):
        return False

    length = len(items)
    if length < 1 or length > 10:
        return False

    for item in items:
        if not isinstance(item, str):
            return False
        if item.strip() == '':
            return False

    return True

